    some disj T1, T2: Track {
        // Universe
        Track = T1 + T2
        Junction = none
        Entry = T2
        Exit = T2
        Signal = none
        Semaphore = none
        Speed = none
        succs = T1->T2
        signals = none->none

        // Requirements (these are enforced; contradiction arises from the Entry condition)
        some Entry and some Exit
