parse:
run Pos1 {
    some disj P1,P2 : Person | some disj C1 : Course | some disj X1 : Project | some disj G1,G2 : Grade {
        Person = P1 + P2
        Professor = P1
        Student = P2
        Course = C1
        Project = X1
        Grade = G1 + G2

        Person <: teaches = P1->C1
        Person <: enrolled = P2->C1
        Person <: projects = P2->X1

        Course <: projects = C1->X1
        Course <: grades = none

        Grade <: next = G1->G2
        // Positive: P2 (a Student) works on X1, and P2 is enrolled in C1 which proposes X1.
        // Other requirements: only students enrolled/work on projects; P1 (Professor) teaches C1; X1 has a worker; each project proposed by exactly one course.
    }
} for 2 Person, 1 Course, 1 Project, 2 Grade expect 1

run Pos2 {
    some disj P1,P2 : Person | some disj C1,C2 : Course | some disj X1,X2 : Project | some disj G1,G2 : Grade {
        Person = P1 + P2
        Professor = P1
        Student = P2
        Course = C1 + C2
        Project = X1 + X2
        Grade = G1 + G2

        Person <: teaches = P1->C1 + P1->C2
        Person <: enrolled = P2->C1 + P2->C2
        Person <: projects = P2->X1 + P2->X2

        Course <: projects = C1->X1 + C2->X2
        Course <: grades = none

        Grade <: next = G1->G2
        // Positive: P2 works on X1 and X2, and is enrolled in C1 and C2 which propose X1 and X2, respectively.
        // Other requirements satisfied as in Pos1.
    }
} for 2 Person, 2 Course, 2 Project, 2 Grade expect 1

run Pos3 {
    some disj P1,P2,P3 : Person | some disj C1 : Course | some disj X1,X2 : Project | some disj G1,G2 : Grade {
        Person = P1 + P2 + P3
        Professor = P1
        Student = P2 + P3
        Course = C1
        Project = X1 + X2
        Grade = G1 + G2

        Person <: teaches = P1->C1
        Person <: enrolled = P2->C1 + P3->C1
        Person <: projects = P2->X1 + P3->X2

        Course <: projects = C1->X1 + C1->X2
        Course <: grades = none

        Grade <: next = G1->G2
        // Positive: Each student works only on projects (X1, X2) of C1, and both are enrolled in C1.
        // Other requirements satisfied: only professors teach; each course has a teacher; every project has a student worker; projects proposed by exactly one course.
    }
} for 3 Person, 1 Course, 2 Project, 2 Grade expect 1

run Neg1 {
    some disj P1,P2 : Person | some disj C1 : Course | some disj X1 : Project | some disj G1,G2 : Grade {
        Person = P1 + P2
        Professor = P1
        Student = P2
        Course = C1
        Project = X1
        Grade = G1 + G2

        Person <: teaches = P1->C1
        Person <: enrolled = none
        Person <: projects = P2->X1

        Course <: projects = C1->X1
        Course <: grades = none

        Grade <: next = G1->G2
        // Negative: P2 works on X1 but is not enrolled in C1 (the only course proposing X1), violating "Students only work on projects of courses they are enrolled in".
        // Other requirements are satisfied: only professors teach; course has a teacher; only students work; each project has a worker; each project belongs to exactly one course.
    }
} for 2 Person, 1 Course, 1 Project, 2 Grade expect 0

