valid:
run Negative_Instance {
  some disj P1, P2, S1 : Person | some disj C1 : Course | some disj PR1 : Project | some disj G1, G2 : Grade {
    // Carriers
    Person = P1 + P2 + S1
    Professor = P1 + P2
    Student = S1
    Course = C1
    Project = PR1
    Grade = G1 + G2

    // Teaching and enrollment
    Person <: teaches = P1->C1
    Person <: enrolled = S1->C1

    // Projects (per person and per course)
    Person <: projects = S1->PR1
    Course  <: projects = C1->PR1

    // Grades: includes a professor as a graded person (colleague of the teacher), causing the violation
    Course <: grades = C1->P2->G1

    // Total order on Grade
    Grade <: next = G1->G2
  }
} for 3 Person, 1 Course, 1 Project, 2 Grade expect 0
