run Neg2 {
  some disj W1, W2 : Workstation | some disj H1 : Human, R1 : Robot | some disj C1, C2 : Component {
    // Workstations and structure
    Workstation = W1 + W2
    begin = W1
    end = W2
    succ = W1->W2

    // Workers: W1 has only a human; W2 has only a robot
    Worker = H1 + R1
    Human = H1
    Robot = R1
    workers = W1->H1 + W2->R1

    // Products: two components; no materials. Each component has a part (the other component).
    Product = C1 + C2
    Material = none
    Component = C1 + C2
    parts = C1->C2 + C2->C1
    workstation = C1->W1 + C2->W2

    // Dangerous: both components are dangerous (C2 has dangerous part C1, so it is dangerous too).
    Dangerous = C1 + C2
  }
} for 2 Workstation, 2 Worker, 2 Product expect 0

run Neg2 {
    some disj Prof1,Prof2,Stu1,Stu2 : Person | some disj C1,C2 : Course | some disj P1,P2 : Project | some disj G1,G2 : Grade {
        Person = Prof1 + Prof2 + Stu1 + Stu2
        Professor = Prof1 + Prof2
        Student = Stu1 + Stu2

        Course = C1 + C2
        Project = P1 + P2
        Grade = G1 + G2

        // One professor per course; professors are not enrolled (do not teach themselves or colleagues).
        teaches = Prof1->C1 + Prof2->C2
        enrolled = Stu1->C1 + Stu2->C1 + Stu2->C2

        // Only students work on projects; each project belongs to exactly one course and has a worker.
        Person <: projects = Stu2->P1 + Stu2->P2
        Course <: projects = C1->P1 + C2->P2

        // C1: Stu1 has the highest grade but works on no C1 project (violation). C2: top student (Stu2) works on a C2 project (satisfies).
        grades = C1->Stu1->G2 + C1->Stu2->G1 + C2->Stu2->G2 + C2->Stu1->G1

        // Total order on Grade (G2 is the highest)
        Grade <: next = G1->G2
    }
} for 4 Person, 2 Course, 2 Project, 2 Grade expect 0
