valid:
run Neg1 {
    some disj U1, U2 : User | some disj P1 : Photo | some disj D1 : Day {
        User = U1 + U2
        Influencer = none
        Photo = P1
        Ad = P1
        Day = D1

        date = P1->D1

        // No follows; no self-follows
        follows = none->none

        // U2 is missing the ad P1
        sees = U1->P1

        // Photo posted by exactly one user
        posts = U1->P1

        suggested = none->none
    }
} for 2 User, 1 Photo, 1 Day expect 0

run Neg4 {
    some disj U1, U2, U3 : User | some disj P1, P2 : Photo | some disj D1, D2 : Day {
        User = U1 + U2 + U3
        Influencer = U1
        Photo = P1 + P2
        Ad = P2
        Day = D1 + D2

        date = P1->D1 + P2->D2

        // U3 follows U1; no self-follows
        follows = U3->U1

        // U2 is missing the ad P2; others see it. Non-ad P1 is only seen by follower U3.
        sees = U1->P2 + U3->P2 + U3->P1

        // Each photo posted by exactly one user
        posts = U1->P1 + U2->P2

        suggested = none->none
    }
} for 3 User, 2 Photo, 2 Day expect 0

