previous:
run Neg4 {
    some disj E1, E2, A, B, J, X : Track | some disj sp1, sp2, smA : Signal {
        Track = E1 + E2 + A + B + J + X
        Junction = J
        Entry = E1 + E2
        Exit = X

        Signal = sp1 + sp2 + smA
        Semaphore = smA
        Speed = sp1 + sp2

        succs = E1->A + E2->A + A->B + A->J + B->J + J->X

        signals = E1->sp1 + E2->sp2 + A->smA
        // Negative: B is immediately before the junction but has no semaphore (violates the requirement).
        // Other constraints hold: at least one entry/exit; exits are exactly sinks; entries are exactly sources;
        // J has >1 predecessor; entries have speeds; acyclic; every entry reaches the exit; non-pre-junction tracks (E1,E2,J,X) have no semaphores.
    }
} for 6 Track, 3 Signal expect 0

parse:
run Pos1 {
  some disj P1, S1 : Person | some disj C1 : Course | some disj G1, G2 : Grade {
    // Universe
    Person = P1 + S1
    Professor = P1
    Student = S1
    Course = C1
    Project = none
    Grade = G1 + G2

    // Teaching and enrollment
    teaches = P1->C1
    enrolled = S1->C1

    // Projects (none)
    Person <: projects = none
    Course <: projects = none

    // Grades (total order and per-course mapping)
    Course <: grades = C1->P1->G1 + C1->S1->G2
    Grade <: next = G1->G2
  }
} for 2 Person, 1 Course, 0 Project, 2 Grade expect 1

