requirements.instances:
run positive1 {
    some disj User1, User2 : User | some disj Photo1, Ad1 : Photo {
        User = User1 + User2
        no Influencer
        Photo = Photo1 + Ad1
        Ad = Ad1
        some disj Day1 : Day {
            Day = Day1
            date = Photo1->Day1 + Ad1->Day1
        }
        follows = User1->User2
        sees = User1->Photo1 + User1->Ad1 + User2->Ad1  // User1 sees Ad1 posted by User2 and Photo1 posted by followed User2
        posts = User2->Photo1 + User2->Ad1
        no suggested
    }
} for 2 User, 2 Photo, 1 Day expect 1

run positive2 {
    some disj User1, User2, User3 : User | some disj Ad1, Ad2 : Photo {
        User = User1 + User2 + User3
        no Influencer
        Photo = Ad1 + Ad2
        Ad = Ad1 + Ad2
        some disj Day1 : Day {
            Day = Day1
            date = Ad1->Day1 + Ad2->Day1
        }
        follows = User1->User2
        sees = User1->Ad1 + User1->Ad2 + User2->Ad1 + User2->Ad2 + User3->Ad1 + User3->Ad2  // All users see all ads
        posts = User2->Ad1 + User3->Ad2
        no suggested
    }
} for 3 User, 2 Photo, 1 Day expect 1

