requirements.instances:
// Positive instance 1: One influencer followed by two regular users
run positive1 {
    some disj u1, u2, u3 : User | some disj p1, p2 : Photo | some disj d1 : Day {
        User = u1 + u2 + u3
        Influencer = u1
        Photo = p1 + p2
        no Ad
        Day = d1
        follows = u2->u1 + u3->u1  // Everyone follows the influencer
        posts = u1->p1 + u2->p2  // Each photo posted by one user
        sees = u1->p1 + u2->p1 + u2->p2 + u3->p1 + u3->p2  // Users see posts from followed users
        no suggested
        date = p1->d1 + p2->d1
    }
} for 3 User, 2 Photo, 1 Day expect 1

// Positive instance 2: Two influencers, each followed by the other and one regular user
run positive2 {
    some disj u1, u2, u3 : User | some disj p1 : Photo | some disj d1 : Day {
        User = u1 + u2 + u3
        Influencer = u1 + u2
        Photo = p1
        no Ad
        Day = d1
        follows = u2->u1 + u3->u1 + u3->u2  // Regular user follows both influencers, u2 follows u1
        posts = u1->p1  // Photo posted by one user
        sees = u1->p1 + u2->p1 + u3->p1  // All see the post from followed user
        no suggested
        date = p1->d1
    }
} for 3 User, 1 Photo, 1 Day expect 1

// Positive instance 3: One influencer with no other users (vacuously true)
run positive3 {
    some disj u1 : User | some disj p1 : Photo | some disj a1 : Ad | some disj d1 : Day {
        User = u1
        Influencer = u1
        Photo = p1 + a1
        Ad = a1
        Day = d1
        no follows  // No one to follow (vacuously satisfied)
        posts = u1->p1 + u1->a1  // User posts both photos
        sees = u1->p1 + u1->a1  // User sees own posts
        no suggested
        date = p1->d1 + a1->d1
    }
} for 1 User, 2 Photo, 1 Day expect 1

