requirements.instances:
run PositiveInstance1 {
    some disj User1 : User | some disj Photo1 : Photo | some disj Day1 : Day {
        User = User1
        no Influencer
        Photo = Photo1
        no Ad
        Day = Day1
        no follows
        no sees
        posts = User1->Photo1
        no suggested
        date = Photo1->Day1
    }
} for 1 User, 1 Photo, 1 Day expect 1
// Positive: Photo1 is posted by exactly one user (User1)

run PositiveInstance2 {
    some disj User1, User2 : User | some disj Photo1, Photo2 : Photo | some disj Day1 : Day {
        User = User1 + User2
        no Influencer
        Photo = Photo1 + Photo2
        no Ad
        Day = Day1
        no follows
        no sees
        posts = User1->Photo1 + User2->Photo2
        no suggested
        date = Photo1->Day1 + Photo2->Day1
    }
} for 2 User, 2 Photo, 1 Day expect 1
// Positive: Each photo is posted by exactly one user (Photo1 by User1, Photo2 by User2)

run PositiveInstance3 {
    some disj Day1 : Day {
        no User
        no Photo
        no follows
        no sees
        no posts
        no suggested
        no date
        Day = Day1
    }
} for 0 User, 0 Photo, 1 Day expect 1
// Positive: No photos exist, so the requirement is vacuously satisfied

run NegativeInstance1 {
    some disj User1, User2 : User | some disj Photo1 : Photo | some disj Day1 : Day {
        User = User1 + User2
        no Influencer
        Photo = Photo1
        no Ad
        Day = Day1
        no follows
        no sees
        posts = User1->Photo1 + User2->Photo1
        no suggested
        date = Photo1->Day1
    }
} for 2 User, 1 Photo, 1 Day expect 0
// Negative: Photo1 is posted by two users (User1 and User2), violating the requirement

run NegativeInstance2 {
    some disj User1 : User | some disj Photo1 : Photo | some disj Day1 : Day {
        User = User1
        no Influencer
        Photo = Photo1
        no Ad
        Day = Day1
        no follows
        no sees
        no posts
        no suggested
        date = Photo1->Day1
    }
} for 1 User, 1 Photo, 1 Day expect 0
// Negative: Photo1 is not posted by any user, violating the requirement

run NegativeInstance3 {
    some disj User1, User2, User3 : User | some disj Photo1, Photo2 : Photo | some disj Day1 : Day {
        User = User1 + User2 + User3
        no Influencer
        Photo = Photo1 + Photo2
        no Ad
        Day = Day1
        no follows
        no sees
        posts = User1->Photo1 + User2->Photo1 + User3->Photo2
        no suggested
        date = Photo1->Day1 + Photo2->Day1
    }
} for 3 User, 2 Photo, 1 Day expect 0
// Negative: Photo1 is posted by two users (User1 and User2), while Photo2 is posted by one user, violating the requirement for Photo1

