requirements.instances:
run PositiveInstance1 {
    some disj User1, User2 : User | some disj Photo1 : Photo | some disj Day1 : Day {
        User = User1 + User2
        no Influencer
        Photo = Photo1
        no Ad
        Day = Day1
        follows = User1->User2
        sees = User1->Photo1 + User2->Photo1
        posts = User1->Photo1
        suggested = User2->User1
        date = Photo1->Day1
    }
} for 2 User, 1 Photo, 1 Day expect 1
// Positive: User1 follows User2 but not themselves, and Photo1 is posted by User1

run PositiveInstance2 {
    some disj User1, User2, User3 : User | some disj Photo1, Photo2 : Photo | some disj Day1, Day2 : Day {
        User = User1 + User2 + User3
        no Influencer
        Photo = Photo1 + Photo2
        no Ad
        Day = Day1 + Day2
        follows = User1->User2 + User2->User3
        sees = User1->Photo1 + User2->Photo2 + User3->Photo1
        posts = User1->Photo1 + User2->Photo2
        suggested = User3->User1
        date = Photo1->Day1 + Photo2->Day2
    }
} for 3 User, 2 Photo, 2 Day expect 1
// Positive: No user follows themselves, User1 follows User2, User2 follows User3, and each photo is posted by exactly one user

run PositiveInstance3 {
    some disj User1 : User | some disj Photo1 : Photo | some disj Day1 : Day {
        User = User1
        no Influencer
        Photo = Photo1
        no Ad
        Day = Day1
        no follows
        sees = User1->Photo1
        posts = User1->Photo1
        no suggested
        date = Photo1->Day1
    }
} for 1 User, 1 Photo, 1 Day expect 1
// Positive: Single user doesn't follow anyone (including themselves), and Photo1 is posted by User1

run NegativeInstance1 {
