        Photo = Photo1
        no Ad
        Day = Day1
        follows = User1->User1 + User1->User2
        sees = User1->Photo1 + User2->Photo1
        posts = User1->Photo1
        suggested = User2->User1
        date = Photo1->Day1
    }
} for 2 User, 1 Photo, 1 Day expect 0
// Negative: User1 follows themselves (User1->User1), violating the requirement

run NegativeInstance2 {
    some disj User1, User2 : User | some disj Photo1 : Photo | some disj Day1 : Day {
        User = User1 + User2
        no Influencer
        Photo = Photo1
        no Ad
        Day = Day1
        follows = User1->User2 + User2->User2
        sees = User1->Photo1 + User2->Photo1
        posts = User2->Photo1
        suggested = User1->User2
        date = Photo1->Day1
    }
} for 2 User, 1 Photo, 1 Day expect 0
// Negative: User2 follows themselves (User2->User2), violating the requirement

run NegativeInstance3 {
    some disj User1, User2, User3 : User | some disj Photo1, Photo2 : Photo | some disj Day1, Day2 : Day {
        User = User1 + User2 + User3
        no Influencer
        Photo = Photo1 + Photo2
        no Ad
        Day = Day1 + Day2
        follows = User1->User2 + User2->User3 + User3->User3
        sees = User1->Photo1 + User2->Photo2 + User3->Photo1
        posts = User1->Photo1 + User2->Photo2
        suggested = User3->User1
        date = Photo1->Day1 + Photo2->Day2
    }
} for 3 User, 2 Photo, 2 Day expect 0
// Negative: User3 follows themselves (User3->User3), violating the requirement

