requirements.instances:
run Positive1 {
	// The only photo P1 is posted by exactly one user U1.
	some disj U1: User | some disj P1: Photo | some disj D1: Day {
		User = U1
		no Influencer
		Photo = P1
		no Ad
		Day = D1
		no follows
		no sees
		posts = U1 -> P1
		no suggested
		date = P1 -> D1
	}
} for 1 User, 1 Photo, 1 Day expect 1

run Positive2 {
	// The requirement holds vacuously since there are no photos.
	some disj U1: User {
		User = U1
		no Influencer
		no Photo
		no Ad
		no Day
		no follows
		no sees
		no posts
		no suggested
		no date
	}
} for 1 User, 0 Photo, 0 Day expect 1

run Positive3 {
	// Photo P1 is posted by exactly one user (U1) and photo P2 is posted by exactly one user (U2).
	some disj U1, U2: User | some disj P1, P2: Photo | some disj D1: Day {
		User = U1 + U2
		Influencer = U1
		Photo = P1 + P2
		Ad = P2
		Day = D1
		no follows
		no sees
		posts = U1 -> P1 + U2 -> P2
		no suggested
		date = P1 -> D1 + P2 -> D1
	}
} for 2 User, 2 Photo, 1 Day expect 1

run Negative1 {
	// The requirement is violated because photo P1 is not posted by any user.
	some disj U1: User | some disj P1: Photo | some disj D1: Day {
		User = U1
		no Influencer
		Photo = P1
		no Ad
		Day = D1
		no follows
		no sees
		no posts
		no suggested
		date = P1 -> D1
	}
} for 1 User, 1 Photo, 1 Day expect 0

run Negative2 {
	// The requirement is violated because photo P1 is posted by two users (U1 and U2).
	some disj U1, U2: User | some disj P1: Photo | some disj D1: Day {
		User = U1 + U2
		no Influencer
		Photo = P1
		no Ad
		Day = D1
		no follows
		no sees
		posts = U1 -> P1 + U2 -> P1
		no suggested
		date = P1 -> D1
	}
} for 2 User, 1 Photo, 1 Day expect 0

run Negative3 {
	// The requirement is violated because photo P2 is not posted by any user, even though P1 is posted by one user.
	some disj U1: User | some disj P1, P2: Photo | some disj D1: Day {
		User = U1
		no Influencer
		Photo = P1 + P2
		no Ad
		Day = D1
		no follows
		no sees
		posts = U1 -> P1
		no suggested
		date = P1 -> D1 + P2 -> D1
	}
} for 1 User, 2 Photo, 1 Day expect 0

