requirements.instances:
run Positive1 {
	// This is a positive instance because there is one user that does not follow anyone,
	// so they do not follow themselves. The requirement on photos is vacuously true since there are no photos.
	some disj User1 : User {
		User = User1
		no Influencer
		no Photo
		no Ad
		no Day
		no follows
		no sees
		no posts
		no suggested
		no date
	}
} for 1 User, 0 Photo, 0 Day expect 1

run Positive2 {
	// This is a positive instance because there are two users and one follows the other,
	// but neither follows themselves. The single photo is posted by exactly one user.
	some disj User1, User2 : User | some disj Photo1 : Photo | some disj Day1 : Day {
		User = User1 + User2
		no Influencer
		Photo = Photo1
		no Ad
		Day = Day1
		follows = User1 -> User2
		no sees
		posts = User1 -> Photo1
		no suggested
		date = Photo1 -> Day1
	}
} for 2 User, 1 Photo, 1 Day expect 1

run Positive3 {
	// This is a positive instance because no user follows anyone, so no user follows themselves.
	// One user is an influencer and one photo is an ad. Both photos are posted by exactly one user.
	some disj User1, User2 : User | some disj Photo1, Photo2 : Photo | some disj Day1 : Day {
		User = User1 + User2
		Influencer = User1
		Photo = Photo1 + Photo2
		Ad = Photo1
		Day = Day1
		no follows
		sees = User1 -> Photo2
		posts = User1 -> Photo1 + User2 -> Photo2
		suggested = User1 -> User2
		date = Photo1 -> Day1 + Photo2 -> Day1
	}
} for 2 User, 2 Photo, 1 Day expect 1

run Negative1 {
	// This is a negative instance because the only user follows themselves.
	// The requirement on photos is vacuously true since there are no photos.
	some disj User1 : User {
		User = User1
		no Influencer
		no Photo
		no Ad
		no Day
		follows = User1 -> User1
		no sees
		no posts
		no suggested
		no date
	}
} for 1 User, 0 Photo, 0 Day expect 0

run Negative2 {
	// This is a negative instance because User2 follows themselves.
	// The requirement that every photo is posted by one user is satisfied.
	some disj User1, User2 : User | some disj Photo1 : Photo | some disj Day1 : Day {
		User = User1 + User2
		Influencer = User2
		Photo = Photo1
		no Ad
		Day = Day1
		follows = User1 -> User2 + User2 -> User2
		no sees
		posts = User1 -> Photo1
		no suggested
		date = Photo1 -> Day1
	}
} for 2 User, 1 Photo, 1 Day expect 0

run Negative3 {
	// This is a negative instance because both users follow themselves.
	// The requirement that every photo is posted by one user is satisfied.
	some disj User1, User2 : User | some disj Photo1, Photo2 : Photo | some disj Day1 : Day {
		User = User1 + User2
		no Influencer
		Photo = Photo1 + Photo2
		Ad = Photo1 + Photo2
		Day = Day1
		follows = User1 -> User1 + User2 -> User2
		no sees
		posts = User1 -> Photo1 + User2 -> Photo2
		no suggested
		date = Photo1 -> Day1 + Photo2 -> Day1
	}
} for 2 User, 2 Photo, 1 Day expect 0

