requirements.instances:
run Positive1 {
	// This is a positive instance because no user posts an ad.
	// The condition "a user posts an ad" is false for all users, so the requirement holds vacuously.
	some disj User1 : User | some disj Photo1 : Photo | some disj Day1 : Day {
		User = User1
		Photo = Photo1
		Day = Day1
		no Influencer
		no Ad
		follows = no User->User
		sees = no User->Photo
		posts = User1->Photo1
		suggested = no User->User
		date = Photo1->Day1
	}
} for 1 User, 1 Photo, 1 Day expect 1

run Positive2 {
	// This is a positive instance because User1 posts an ad, and all of User1's posts are ads.
	some disj User1 : User | some disj Photo1, Photo2 : Photo | some disj Day1 : Day {
		User = User1
		Photo = Photo1 + Photo2
		Day = Day1
		no Influencer
		Ad = Photo1 + Photo2
		follows = no User->User
		sees = User1->Photo1 + User1->Photo2
		posts = User1->Photo1 + User1->Photo2
		suggested = no User->User
		date = Photo1->Day1 + Photo2->Day1
	}
} for 1 User, 2 Photo, 1 Day expect 1

run Positive3 {
	// This is a positive instance because User1 posts an ad and all their posts are ads,
	// while User2 only posts a non-ad, which does not trigger the condition.
	some disj User1, User2 : User | some disj Photo1, Photo2 : Photo | some disj Day1 : Day {
		User = User1 + User2
		Photo = Photo1 + Photo2
		Day = Day1
		no Influencer
		Ad = Photo1
		follows = User1->User2
		sees = User1->Photo1 + User2->Photo1 + User1->Photo2
		posts = User1->Photo1 + User2->Photo2
		suggested = no User->User
		date = Photo1->Day1 + Photo2->Day1
	}
} for 2 User, 2 Photo, 1 Day expect 1

run Negative1 {
	// This is a negative instance because User1 posts an ad (Photo1)
	// but also posts a non-ad (Photo2).
	some disj User1 : User | some disj Photo1, Photo2 : Photo | some disj Day1 : Day {
		User = User1
		Photo = Photo1 + Photo2
		Day = Day1
		no Influencer
		Ad = Photo1
		follows = no User->User
		sees = User1->Photo1
		posts = User1->Photo1 + User1->Photo2
		suggested = no User->User
		date = Photo1->Day1 + Photo2->Day1
	}
} for 1 User, 2 Photo, 1 Day expect 0

run Negative2 {
	// This is a negative instance because Influencer1 posts an ad (Photo1)
	// but also posts a non-ad (Photo2).
	some disj Influencer1 : Influencer | some disj Photo1, Photo2 : Photo | some disj Day1 : Day {
		User = Influencer1
		Photo = Photo1 + Photo2
		Day = Day1
		Influencer = Influencer1
		Ad = Photo1
		follows = no User->User
		sees = Influencer1->Photo1
		posts = Influencer1->Photo1 + Influencer1->Photo2
		suggested = no User->User
		date = Photo1->Day1 + Photo2->Day1
	}
} for 1 User, 2 Photo, 1 Day expect 0

run Negative3 {
	// This is a negative instance because User1 posts an ad (Photo1) and a non-ad (Photo2).
	// User2 is compliant as they post no ads. The instance is invalid due to User1.
	some disj User1, User2 : User | some disj Photo1, Photo2, Photo3 : Photo | some disj Day1 : Day {
		User = User1 + User2
		Photo = Photo1 + Photo2 + Photo3
		Day = Day1
		no Influencer
		Ad = Photo1
		follows = User2->User1
		sees = User1->Photo1 + User2->Photo1 + User2->Photo2
		posts = User1->Photo1 + User1->Photo2 + User2->Photo3
		suggested = no User->User
		date = Photo1->Day1 + Photo2->Day1 + Photo3->Day1
	}
} for 2 User, 3 Photo, 1 Day expect 0

