requirements.instances:
run Positive1 {
	// This is a positive instance because there is one influencer (I1) and one
	// other user (U1), and U1 follows I1, as required.
	some disj I1, U1 : User {
		User = I1 + U1
		Influencer = I1
		no Photo
		no Ad
		no Day
		follows = U1->I1
		no sees
		no posts
		no suggested
		no date
	}
} for 2 User, 0 Photo, 0 Day expect 1

run Positive2 {
	// This is a positive instance because there are no influencers, so the
	// requirement is vacuously true. All other requirements are met.
	some disj U1, U2 : User | some disj P1 : Photo | some disj D1 : Day {
		User = U1 + U2
		no Influencer
		Photo = P1
		Ad = P1
		Day = D1
		follows = U1->U2 + U2->U1
		// P1 is an ad, so everyone sees it.
		sees = U1->P1 + U2->P1
		// U1 posts an ad, and all of U1's posts are ads.
		posts = U1->P1
		no suggested
		date = P1->D1
	}
} for 2 User, 1 Photo, 1 Day expect 1

run Positive3 {
	// This is a positive instance because the two influencers (I1, I2) are
	// followed by everyone else (U1 follows both, and they follow each other).
	some disj I1, I2, U1 : User | some disj P1 : Photo | some disj D1 : Day {
		User = I1 + I2 + U1
		Influencer = I1 + I2
		Photo = P1
		no Ad
		Day = D1
		follows = U1->I1 + U1->I2 + I1->I2 + I2->I1
		// P1 is a non-ad posted by U1. No one follows U1, so no one sees it.
		no sees
		posts = U1->P1
		no suggested
		date = P1->D1
	}
} for 3 User, 1 Photo, 1 Day expect 1

run Negative1 {
	// This is a negative instance because there is an influencer (I1) that is
	// not followed by another user (U2).
	some disj I1, U1, U2 : User {
		User = I1 + U1 + U2
		Influencer = I1
		no Photo
		no Ad
		no Day
		follows = U1->I1
		no sees
		no posts
		no suggested
		no date
	}
} for 3 User, 0 Photo, 0 Day expect 0

run Negative2 {
	// This is a negative instance because there are two influencers (I1, I2),
	// but I1 does not follow I2, violating the requirement that influencers
	// are followed by everyone else.
	some disj I1, I2, U1 : User | some disj P1 : Photo | some disj D1 : Day {
		User = I1 + I2 + U1
		Influencer = I1 + I2
		Photo = P1
		no Ad
		Day = D1
		follows = U1->I1 + U1->I2 + I2->I1
		// P1 is a non-ad posted by I1. It is seen by U1 and I2, who follow I1.
		sees = U1->P1 + I2->P1
		posts = I1->P1
		no suggested
		date = P1->D1
	}
} for 3 User, 1 Photo, 1 Day expect 0

run Negative3 {
	// This is a negative instance because influencer I1 is not followed by
	// user U1.
	some disj I1, U1 : User | some disj P1 : Photo | some disj D1 : Day {
		User = I1 + U1
		Influencer = I1
		Photo = P1
		Ad = P1
		Day = D1
		no follows
		// P1 is an ad, so everyone sees it.
		sees = I1->P1 + U1->P1
		posts = U1->P1
		no suggested
		date = P1->D1
	}
} for 2 User, 1 Photo, 1 Day expect 0

