run Positive1 {
	// This is a positive instance because U1 follows U2 and U2 follows U3,
	// so U3 is correctly suggested to U1, as U3 is not U1 and is not already followed by U1.
	some disj U1, U2, U3: User | some disj P1, P2, P3: Photo | some D1: Day {
		User = U1 + U2 + U3
		no Influencer
		Photo = P1 + P2 + P3
		no Ad
		Day = D1
		follows = U1->U2 + U2->U3
		posts = U1->P1 + U2->P2 + U3->P3
		sees = U1->P2 + U2->P3
		suggested = U1->U3
		date = P1->D1 + P2->D1 + P3->D1
	}
} for 3 User, 3 Photo, 1 Day expect 1

run Positive2 {
	// This is a positive instance with an influencer. U1 follows influencer I1,
	// and I1 follows U2. U2 is correctly suggested to U1. U2 is not suggested
	// to himself because the suggestion logic excludes the user.
	some disj U1, U2, I1: User | some disj P1, P2, P3: Photo | some D1: Day {
		User = U1 + U2 + I1
		Influencer = I1
		Photo = P1 + P2 + P3
		Ad = P1
		Day = D1
		follows = U1->I1 + U2->I1 + I1->U2
		posts = I1->P1 + U1->P2 + U2->P3
		sees = U1->P1 + U2->P1 + I1->(P1 + P3)
		suggested = U1->U2
		date = P1->D1 + P2->D1 + P3->D1
	}
} for 3 User, 3 Photo, 1 Day expect 1

run Positive3 {
	// This is a positive instance where a user follows multiple other users.
	// U1 follows U2 and U3. Both U2 and U3 follow U4.
	// U4 is correctly suggested to U1.
	some disj U1, U2, U3, U4: User | some disj P1, P2, P3, P4: Photo | some D1: Day {
		User = U1 + U2 + U3 + U4
		no Influencer
		Photo = P1 + P2 + P3 + P4
		Ad = P4
		Day = D1
		follows = U1->U2 + U1->U3 + U2->U4 + U3->U4
		posts = U1->P1 + U2->P2 + U3->P3 + U4->P4
		sees = U1->(P2 + P3 + P4) + U2->P4 + U3->P4 + U4->P4
		suggested = U1->U4
		date = P1->D1 + P2->D1 + P3->D1 + P4->D1
	}
} for 4 User, 4 Photo, 1 Day expect 1

run Negative1 {
	// This is a negative instance because U1 is suggested U2,
	// but U1 already follows U2, which violates the requirement.
	some disj U1, U2, U3: User | some disj P1, P2, P3: Photo | some D1: Day {
		User = U1 + U2 + U3
		no Influencer
		Photo = P1 + P2 + P3
		no Ad
		Day = D1
		follows = U1->U2 + U2->U3
		posts = U1->P1 + U2->P2 + U3->P3
		sees = U1->P2 + U2->P3
		suggested = U1->(U2 + U3)
		date = P1->D1 + P2->D1 + P3->D1
	}
} for 3 User, 3 Photo, 1 Day expect 0

run Negative2 {
	// This is a negative instance because U3 should be suggested to U1
	// (since U1 follows U2 and U2 follows U3), but there are no suggestions.
	some disj U1, U2, U3: User | some disj P1, P2, P3: Photo | some D1: Day {
		User = U1 + U2 + U3
		no Influencer
		Photo = P1 + P2 + P3
		no Ad
		Day = D1
		follows = U1->U2 + U2->U3
		posts = U1->P1 + U2->P2 + U3->P3
		sees = U1->P2 + U2->P3
		no suggested
		date = P1->D1 + P2->D1 + P3->D1
	}
} for 3 User, 3 Photo, 1 Day expect 0

run Negative3 {
	// This is a negative instance because U3 is suggested to U1, but U3 is not
	// followed by any user that U1 follows (U1 only follows U2, who follows no one).
	some disj U1, U2, U3, U4: User | some disj P1, P2, P3, P4: Photo | some D1: Day {
		User = U1 + U2 + U3 + U4
		no Influencer
		Photo = P1 + P2 + P3 + P4
		no Ad
		Day = D1
		follows = U1->U2
		posts = U1->P1 + U2->P2 + U3->P3 + U4->P4
		sees = U1->P2
		suggested = U1->U3
		date = P1->D1 + P2->D1 + P3->D1 + P4->D1
	}
} for 4 User, 4 Photo, 1 Day expect 0

