	// No users exist, so the requirement is vacuously satisfied.
	no User
	no Influencer
	no Photo
	no Ad
	no Day
	no follows
	no sees
	no posts
	no suggested
	no date
} for 0 User, 0 Photo, 0 Day expect 1

run Positive2 {
	// A single user exists but does not follow anyone, satisfying the requirement.
	some disj U1: User | some disj P1: Photo | some disj D1: Day {
		User = U1
		no Influencer
		Photo = P1
		no Ad
		Day = D1
		no follows
		no sees
		posts = U1->P1
		no suggested
		date = P1->D1
	}
} for 1 User, 1 Photo, 1 Day expect 1

run Positive3 {
	// Two users exist, one follows the other but not themselves.
	some disj U1, U2: User | some disj P1: Photo | some disj D1: Day {
		User = U1 + U2
		Influencer = U1
		Photo = P1
		no Ad
		Day = D1
		follows = U1->U2
		sees = U2->P1
		posts = U1->P1
		suggested = U2->U1
		date = P1->D1
	}
} for 2 User, 1 Photo, 1 Day expect 1

