requirements.instances:
// Positive instances
run Positive1 {
	some disj U1, U2: User | some disj P1, A1: Photo | some disj D1, D2: Day {
		// All sets
		User = U1 + U2
		no Influencer
		Photo = P1 + A1
		Ad = A1
		Day = D1 + D2
		// All relations
		follows = U1 -> U2
		sees = U1->A1 + U2->A1 + U1->P1
		posts = U2->P1 + U1->A1
		no suggested
		date = P1->D1 + A1->D2
	}
} for 2 User, 2 Photo, 2 Day expect 1 // Positive instance. All users see the ad A1. User U1 sees the non-ad P1 because U1 follows its poster U2. User U2 does not see the non-ad P1 because U2 does not follow its poster (U2).

run Positive2 {
	some disj U1, U2: User | some A1: Photo | some D1: Day {
		// All sets
		User = U1 + U2
		no Influencer
		Photo = A1
		Ad = A1
		Day = D1
		// All relations
		no follows
		sees = U1->A1 + U2->A1
		posts = U1->A1
		no suggested
		date = A1->D1
	}
} for 2 User, 1 Photo, 1 Day expect 1 // Positive instance. All users see the ad A1. The condition on non-ads is vacuously true as there are no non-ad photos.

run Positive3 {
	some disj U1, U2: User | some disj P1, P2: Photo | some disj D1, D2: Day {
		// All sets
		User = U1 + U2
		no Influencer
		Photo = P1 + P2
		no Ad
		Day = D1 + D2
		// All relations
		no follows
		no sees
		posts = U1->P1 + U2->P2
		no suggested
		date = P1->D1 + P2->D2
	}
} for 2 User, 2 Photo, 2 Day expect 1 // Positive instance. The condition on ads is vacuously true as there are no ads. The condition on non-ads is also satisfied as no user sees any non-ad photo.

// Negative instances
run Negative1 {
	some disj U1, U2: User | some A1: Photo | some D1: Day {
		// All sets
		User = U1 + U2
		no Influencer
		Photo = A1
		Ad = A1
		Day = D1
		// All relations
		no follows
		sees = U1->A1 // U2 does not see the ad A1
		posts = U1->A1
		no suggested
		date = A1->D1
	}
} for 2 User, 1 Photo, 1 Day expect 0 // Negative instance. User U2 does not see the ad A1, violating the requirement that users see all ads.

run Negative2 {
	some disj U1, U2: User | some P1: Photo | some D1: Day {
		// All sets
		User = U1 + U2
		no Influencer
		Photo = P1
		no Ad
		Day = D1
		// All relations
		no follows
		sees = U1->P1 // U1 sees P1 but does not follow its poster U2
		posts = U2->P1
		no suggested
