requirements.instances:
run Pos1 {
    // Positive: There are no photos, so the requirement holds vacuously.
    no User
    no Photo
    no Day
    no Influencer
    no Ad
    no follows
    no sees
    no posts
    no suggested
    no date
} for 0 User, 0 Photo, 0 Day expect 1

run Pos2 {
    // Positive: The single photo is posted by exactly one user.
    some disj U1 : User | some disj P1 : Photo | some disj D1 : Day {
        User = U1
        Photo = P1
        Day = D1
        Influencer = U1
        Ad = P1
        no follows
        no sees
        posts = U1->P1
        no suggested
        date = P1->D1
    }
} for 1 User, 1 Photo, 1 Day expect 1

run Pos3 {
    // Positive: Each photo is posted by exactly one (distinct) user.
    some disj U1, U2 : User | some disj P1, P2 : Photo | some disj D1, D2 : Day {
        User = U1 + U2
        Photo = P1 + P2
        Day = D1 + D2
        Influencer = U2
        Ad = P2
        no follows
        no sees
        posts = U1->P1 + U2->P2
        no suggested
        date = P1->D1 + P2->D2
    }
} for 2 User, 2 Photo, 2 Day expect 1

run Neg1 {
    // Negative: The photo is posted by no user (violates "posted by one user").
    some disj U1 : User | some disj P1 : Photo | some disj D1 : Day {
        User = U1
        Photo = P1
        Day = D1
        no Influencer
        no Ad
        no follows
        no sees
        no posts
        no suggested
        date = P1->D1
    }
} for 1 User, 1 Photo, 1 Day expect 0

run Neg2 {
    // Negative: The single photo is posted by two users (violates uniqueness).
    some disj U1, U2 : User | some disj P1 : Photo | some disj D1 : Day {
        User = U1 + U2
        Photo = P1
        Day = D1
        Influencer = U1
        no Ad
        no follows
        no sees
        posts = U1->P1 + U2->P1
        no suggested
        date = P1->D1
    }
} for 2 User, 1 Photo, 1 Day expect 0

run Neg3 {
    // Negative: There is a photo but no users exist, so no user can have posted it.
    some disj P1 : Photo | some disj D1 : Day {
        no User
        Photo = P1
        Day = D1
        no Influencer
        no Ad
        no follows
        no sees
        no posts
        no suggested
        date = P1->D1
    }
} for 0 User, 1 Photo, 1 Day expect 0

