requirements.instances:
run Pos1 {
    // Positive: All users see the ad P1; only U1 sees non-ad P2 because U1 follows its poster U2; no user follows themselves; each photo is posted by exactly one user.
    some disj U1, U2 : User | some disj P1, P2 : Photo | some disj D1, D2 : Day {
        User = U1 + U2
        no Influencer
        Photo = P1 + P2
        Ad = P1
        Day = D1 + D2
        date = P1->D1 + P2->D2
        posts = U1->P1 + U2->P2
        follows = U1->U2
        no suggested
        sees = U1->P1 + U2->P1 + U1->P2
    }
} for 2 User, 2 Photo, 2 Day expect 1

run Pos2 {
    // Positive: Only an ad exists (P1), posted by U3; every user sees this ad; no self-follows; every photo is posted by exactly one user.
    some disj U1, U2, U3 : User | some disj P1 : Photo | some disj D1 : Day {
        User = U1 + U2 + U3
        Influencer = U2
        Photo = P1
        Ad = P1
        Day = D1
        date = P1->D1
        posts = U3->P1
        no follows
        no suggested
        sees = U1->P1 + U2->P1 + U3->P1
    }
} for 3 User, 1 Photo, 1 Day expect 1

run Pos3 {
    // Positive: Ads P1 and P2 are seen by all users; non-ad P3 is seen only by followers of its poster U3 (U1 and U2 follow U3); no self-follows; each photo has exactly one poster.
    some disj U1, U2, U3 : User | some disj P1, P2, P3 : Photo | some disj D1, D2, D3 : Day {
        User = U1 + U2 + U3
        Influencer = U1 + U3
        Photo = P1 + P2 + P3
        Ad = P1 + P2
        Day = D1 + D2 + D3
        date = P1->D1 + P2->D2 + P3->D3
        posts = U1->P1 + U2->P2 + U3->P3
        follows = U1->U3 + U2->U3
        no suggested
        sees = 
            // everyone sees ads
            U1->P1 + U2->P1 + U3->P1 +
            U1->P2 + U2->P2 + U3->P2 +
            // only followers see non-ad P3
            U1->P3 + U2->P3
    }
} for 3 User, 3 Photo, 3 Day expect 1

