run Pos1 {
    some disj U1 : User | some disj P1 : Photo | some disj D1 : Day {
        User = U1
        no Influencer
        Photo = P1
        no Ad
        Day = D1
        no follows
        no sees
        posts = U1->P1
        no suggested
        date = P1->D1
        // Positive: the only photo P1 is posted by exactly one user U1.
    }
} for 1 User, 1 Photo, 1 Day expect 1

run Pos2 {
    some disj U1, U2 : User | some disj P1, P2 : Photo | some disj D1, D2 : Day {
        User = U1 + U2
        no Influencer
        Photo = P1 + P2
        no Ad
        Day = D1 + D2
        follows = U2->U1
        no sees
        posts = U1->P1 + U1->P2
        suggested = U1->U2
        date = P1->D1 + P2->D2
        // Positive: each photo (P1 and P2) is posted by exactly one user (U1).
    }
} for 2 User, 2 Photo, 2 Day expect 1

run Pos3 {
    some disj U1, U2 : User | some disj P1, P2 : Photo | some disj D1, D2 : Day {
        User = U1 + U2
        Influencer = U2
        Photo = P1 + P2
        Ad = P2
        Day = D1 + D2
        follows = U1->U2
        sees = U1->P2
        posts = U1->P1 + U2->P2
        suggested = U1->U2
        date = P1->D1 + P2->D2
        // Positive: every photo (including Ad P2) is posted by exactly one user (P1 by U1, P2 by U2).
    }
} for 2 User, 2 Photo, 2 Day expect 1

