requirements.instances:
run Pos1 {
    some disj U1, U2 : User | some disj P1, P2 : Photo | some disj D1 : Day {
        // Positive: U2 sees the ad P1 posted by U1, and U2 follows U1. No non-ads are seen.
        User = U1 + U2
        no Influencer
        Photo = P1 + P2
        Ad = P1
        Day = D1
        date = P1->D1 + P2->D1
        posts = U1->P1 + U2->P2
        follows = U2->U1
        no suggested
        sees = U2->P1
    }
} for 2 User, 2 Photo, 1 Day expect 1

run Pos2 {
    some disj U1, U2, U3 : User | some disj P1 : Photo | some disj D1 : Day {
        // Positive: U1 sees the ad P1 posted by U3; U1 does not follow U3 but has U3 in suggested via U1->U2 and U2->U3. No non-ads are seen.
        User = U1 + U2 + U3
        no Influencer
        Photo = P1
        Ad = P1
        Day = D1
        date = P1->D1
        posts = U3->P1
        follows = U1->U2 + U2->U3
        suggested = U1->U3
        sees = U1->P1
    }
} for 3 User, 1 Photo, 1 Day expect 1

run Pos3 {
    some disj U1 : User {
        // Positive: There are no ads (indeed no photos), so trivially no user sees ads from outside followed or suggested users; other requirements hold vacuously.
        User = U1
        no Influencer
        no Photo
        no Ad
        no Day
        no date
        no posts
        no follows
        no suggested
        no sees
    }
} for 1 User, 0 Photo, 0 Day expect 1

