requirements.instances:
run Positive1 {
    // Positive: the single photo is posted by exactly one user.
    some disj U1: User | some disj P1: Photo | some disj D1: Day {
        User = U1
        Photo = P1
        Day = D1

        no Influencer
        no Ad

        no follows
        no sees
        no suggested
        posts = U1->P1
        date = P1->D1
    }
} for 1 User, 1 Photo, 1 Day expect 1

run Positive2 {
    // Positive: there is one photo and it is posted by exactly one of the two users.
    some disj U1, U2: User | some disj P1: Photo | some disj D1: Day {
        User = U1 + U2
        Photo = P1
        Day = D1

        Influencer = U1
        Ad = P1

        no follows
        no sees
        no suggested
        posts = U2->P1
        date = P1->D1
    }
} for 2 User, 1 Photo, 1 Day expect 1

run Positive3 {
    // Positive: each photo is posted by exactly one user (P1 by U1, P2 by U2).
    some disj U1, U2: User | some disj P1, P2: Photo | some disj D1, D2: Day {
        User = U1 + U2
        Photo = P1 + P2
        Day = D1 + D2

        Influencer = U2
        Ad = P2

        no follows
        no sees
        no suggested
        posts = U1->P1 + U2->P2
        date = P1->D1 + P2->D2
    }
} for 2 User, 2 Photo, 2 Day expect 1

