requirements.instances:
run Pos1 {
    some disj U1 : User | some disj P1 : Photo | some disj D1 : Day {
        // sets
        User = U1
        no Influencer
        Photo = P1
        Ad = P1
        Day = D1

        // relations
        no follows
        posts = U1->P1
        date = P1->D1
        sees = U1->P1
        no suggested

        // Positive: U1 posts only an ad (P1), so all their posts are ads. Other requirements hold (no self-follow; everyone sees the ad; every photo posted by exactly one user).
    }
} for 1 User, 1 Photo, 1 Day expect 1

run Pos2 {
    some disj U1, U2 : User | some disj P1, P2 : Photo | some disj D1, D2 : Day {
        // sets
        User = U1 + U2
        Influencer = U2
        Photo = P1 + P2
        Ad = P1
        Day = D1 + D2

        // relations
        follows = U1->U2
        posts = U1->P1 + U2->P2
        date = P1->D1 + P2->D2
        sees = U1->P1 + U2->P1 + U1->P2
        no suggested

        // Positive: U1 posts only ads (P1) and U2 posts only a non-ad (P2). Ads are seen by everyone; non-ad P2 is seen only by follower U1. No self-follows; each photo posted by exactly one user.
    }
} for 2 User, 2 Photo, 2 Day expect 1

run Pos3 {
    some disj U1, U2 : User | some disj P1, P2 : Photo | some disj D1 : Day {
        // sets
        User = U1 + U2
        Influencer = U1
        Photo = P1 + P2
        no Ad
        Day = D1

        // relations
        follows = U1->U2 + U2->U1
        posts = U1->P1 + U2->P2
        date = P1->D1 + P2->D1
        sees = U1->P2 + U2->P1
        no suggested

        // Positive: No user posts an ad, so the requirement holds vacuously. Non-ads are only seen by followed users; no self-follows; each photo posted by exactly one user.
    }
} for 2 User, 2 Photo, 1 Day expect 1

